INSERT INTO consola (id, nombre, modelo, description, fabricante, precio, cantidad, ventas,fecha_lanzamiento, llegada_al_mercado, imagen) VALUES (1, 'Wii','RVL-001', 'Sexta videoconsola descontinuada producida por Nintendo y estrenada el 19 de noviembre de 2006 en Norteamérica y el 8 de diciembre del mismo año en Europa. Perteneciente a la séptima generación de videoconsolas, es la sucesora directa de GameCube y compitió con la Xbox 360 de Microsoft y la PlayStation 3 de Sony.', 'Nintendo', 99.99,3, 2, '2006-11-12', '2025-04-08', 'https://upload.wikimedia.org/wikipedia/commons/thumb/8/83/Wii_console.png/250px-Wii_console.png');
INSERT INTO consola (id, nombre, modelo, description, fabricante, precio, cantidad, ventas,fecha_lanzamiento, llegada_al_mercado, imagen) VALUES (2, 'Nintendo DS', 'Lite', 'Videoconsola portátil de la multinacional japonesa Nintendo, creada para suceder a la Game Boy Advance. Permite la reproducción de videojuegos y multimedia, y pertenece a la séptima generación cuyo rival directo fue la PlayStation Portable. Fue la tercera consola para videojuegos en la que el jugador puede interactuar con el juego mediante el uso de la pantalla táctil.', 'Nintendo', 69.99,3, 2, '2005-03-11', '2025-04-08','https://upload.wikimedia.org/wikipedia/commons/thumb/5/56/Nintendo-DS-Lite-Black-Open.png/250px-Nintendo-DS-Lite-Black-Open.png');
INSERT INTO consola (id, nombre, modelo, description, fabricante, precio, cantidad, ventas,fecha_lanzamiento, llegada_al_mercado, imagen) VALUES (3, 'Play Station 3', 'FAT','Tercera videoconsola de sobremesa descontinuada producida por Sony Computer Entertainment. Fue la quinta y última consola de Sony en ser diseñada por Ken Kutaragi y formó parte de las videoconsolas de séptima generación y sus competidores fueron la Xbox 360 de Microsoft y la Wii de Nintendo.', 'Sony', 99.99,3, 2, '2007-03-23', '2025-04-08', 'https://upload.wikimedia.org/wikipedia/commons/thumb/9/90/Sony-PlayStation-3-CECHA01-wController-L.jpg/960px-Sony-PlayStation-3-CECHA01-wController-L.jpg?20170727225248');
INSERT INTO consola (id, nombre, modelo, description, fabricante, precio, cantidad, ventas,fecha_lanzamiento, llegada_al_mercado, imagen) VALUES (4, 'XBOX 360', 'FAT','Segunda videoconsola de sobremesa de la marca Xbox producida por Microsoft. Fue desarrollada en colaboración con IBM y ATI (AMD) y lanzada en América del Sur, América del Norte, Japón, Europa y Australia entre 2005 y 2006. Su servicio Xbox Live (el cual es de pago y ya no está activo ) permite a los jugadores competir en línea y descargar contenidos como juegos arcade, demos, tráileres, programas de televisión y películas. La Xbox 360 es la sucesora directa de la Xbox, y compitió con la PlayStation 3 de Sony y Wii de Nintendo como parte de las videoconsolas de séptima generación.', 'Microsoft', 99.99,3, 2, '2005-11-22', '2025-04-08', 'https://upload.wikimedia.org/wikipedia/commons/thumb/4/40/Xbox-360-Pro-wController.jpg/250px-Xbox-360-Pro-wController.jpg');
INSERT INTO consola (id, nombre, modelo, description, fabricante, precio, cantidad, ventas,fecha_lanzamiento, llegada_al_mercado, imagen) VALUES (5, 'PSP', 'PSP-1000','Videoconsola portátil de la multinacional de origen japonés sony, y la cuarta consola de Sony en ser diseñada por Ken Kutaragi, PSP servía para videojuegos, conectarse a internet y reproducir y ver multimedia. Se trata de la primera consola portátil a nivel mundial de Sony y la segunda lanzada en Japón.', 'Sony', 59.99,3, 2, '2004-12-12', '2025-04-08', 'https://upload.wikimedia.org/wikipedia/commons/thumb/5/5d/Sony-PSP-1000-Body.png/250px-Sony-PSP-1000-Body.png');
INSERT INTO juego (nombre, description, precio, cantidad, ventas, genero, num_jugadores, console_id, fecha_lanzamiento, llegada_al_mercado, ruta_imagen) VALUES ('New Super Mario Bros Wii', 'Toma a una princesa secuestrada, un tirano en forma de reptil y un fontanero sin igual, aderézalo con mundos llenos de aventuras y mézclalo todo para crear un nuevo juego de Mario con un sabor familiar. Invita hasta tres amigos más y emprende esta nueva aventura ¡como nunca antes la habías jugado!', 29.99, 1, 0, 'Plataforma', 4, 1, '2009-11-12', '2025-04-30','https://art.gametdb.com/wii/cover3D/ES/SMNP01.png?1317736131');
INSERT INTO juego (nombre, description, precio, cantidad, ventas, genero, num_jugadores, console_id, fecha_lanzamiento, llegada_al_mercado, ruta_imagen) VALUES ('Inazuma Eleven 2 Tormenta de Fuego', 'Quizás hayan ganado el Torneo Fútbol Frontrier, pero Mark y sus amigos van a necesitas habilidades de otro mundo para poder batirse en igualdad de condiciones en Inazuma Eleven 2. El popular juego de rol de fútbol vuelve con una nueva aventura en dos versiones: Inazuma Eleven 2: Tormenta de Fuego e Inazuma Eleven 2: Ventisca Eterna.', 19.99, 2, 1, 'J-RPG', 4, 2, '2012-03-12', '2025-05-06','https://art.gametdb.com/ds/box/EN/BEES.png?1502910585');
INSERT INTO juego (nombre, description, precio, cantidad, ventas, genero, num_jugadores, console_id, fecha_lanzamiento, llegada_al_mercado, ruta_imagen) VALUES ('Cars Race-O-Rama', 'Únete a Rayo McQueen y corre contra nuevos personajes para hacerte con el trofeo de Race-O-Rama. Conduce y personaliza a Rayo McQueen, estrella de la película animada Cars. Enfréntate a nuevos personajes en una serie de pistas emocionantes. Corre contra un amigo en el vertiginoso modo multijugador.', 24.99, 2, 0, 'Carreras', 2, 3, '2009-11-30', '2025-05-05','https://art.gametdb.com/ps3/cover/ES/BLES00620.jpg?1404248226');
INSERT INTO juego (nombre, description, precio, cantidad, ventas, genero, num_jugadores, console_id, fecha_lanzamiento, llegada_al_mercado, ruta_imagen) VALUES ('Cars', 'Únete a Rayo McQueen y corre contra nuevos personajes para hacerte con el trofeo de Race-O-Rama. Conduce y personaliza a Rayo McQueen, estrella de la película animada Cars. Enfréntate a nuevos personajes en una serie de pistas emocionantes. Corre contra un amigo en el vertiginoso modo multijugador.', 24.99, 2, 0, 'Carreras', 2, 4, '2009-11-30', '2025-05-06','https://cdn-products.eneba.com/resized-products/QSJevhfSKVAalNRa477NStt6-iGLy0EjPD_4JU5khec_350x200_1x-0.png');
INSERT INTO juego (nombre, description, precio, cantidad, ventas, genero, num_jugadores, console_id, fecha_lanzamiento, llegada_al_mercado, ruta_imagen) VALUES ('Mario Kart Wii', 'Basta con poner tu mando en el Wii Wheel para ocupar el asiento del conductor. Es tan intuitivo que estarás derrapando, ejecutando piruetas aéreas y haciendo caballitos en menos de lo que canta un gallo. ¡Participa en emocionantes carreras de hasta cuatro jugadores desde una única consola Wii!', 29.99, 3, 3, 'Carreras', 4, 1, '2008-04-10', '2025-05-06', 'https://art.gametdb.com/wii/cover3D/ES/RMCP01.png?1528143761');
INSERT INTO juego (nombre, description, precio, cantidad, ventas, genero, num_jugadores, console_id, fecha_lanzamiento, llegada_al_mercado, ruta_imagen) VALUES ('Profesor Layton y la Llamada del Espectro', 'Únete al Profesor Layton para resolver acertijos y solucionar un nuevo misterio. Este emocionante juego de puzles de más de 170 rompecabezas y nuevos minijuegos para mantenerte entretenido por horas mientras resuelves el misterio de Misthallery.', 14.99, 3, 0, 'Puzzle', 1, 2, '2011-11-25', '2025-05-08', 'https://art.gametdb.com/ds/box/EN/BLFS.png?1488007375');
INSERT INTO juego (nombre, description, precio, cantidad, ventas, genero, num_jugadores, console_id, fecha_lanzamiento, llegada_al_mercado, ruta_imagen) VALUES ('Cars', '¡Haz rugir los motores y sé el primero en cruzar la línea de meta! ¡Los nuevos adversarios han llegado a la ciudad y no partirán hasta que sean vencidos en los circuitos de Radiador Springs! Juego de carreras en el universo de Cars 5 modos de carreras: historia, carreras contrarreloj, Gran Premio, modo entretenimiento y modo multijugador. Todos los vehículos de la película así como determinados modelos exclusivos del videojuego. Cinemáticas que desbloquear 1 a 4 jugadores wireless.', 16.99, 3, 0, 'Carreras', 4, 5, '2006-07-25', '2025-05-08', 'https://media.vandal.net/t200/5637/2011910104151_1.jpg');
INSERT INTO juego (nombre, description, precio, cantidad, ventas, genero, num_jugadores, console_id, fecha_lanzamiento, llegada_al_mercado, ruta_imagen) VALUES ('Titanfall', 'Prepárate para Titanfall. Producido por uno de los co-creadores de Call of Duty y otros desarolladores claves detrás de dicha franquincia, Titanfall es un nuevo universo en el que se yuxtaponen lo pequeño frente a lo gigante, lo natural frente a lo industrial y el hombre frente a la máquina. Los visionarios de Respawn se han inspirado en su probada experiencia en juegos de acción en primera persona y con Titanfall están concentrados en traer algo excitante a la nueva generación de juegos multijugador.', 21.99, 3, 0, 'Shooter', 1, 4, '2006-07-25', '2025-05-08', 'https://http2.mlstatic.com/D_NQ_NP_612999-MLU70683742851_072023-O.webp');